package com.ibm.nili.test.jm.mathematicalfunctions;import java.awt.Canvas;import java.awt.Color;import java.awt.Graphics;// Draws a spiral - simplest fractal interationclass SpiralCanvas extends Canvas {    /**     *      */    private static final long serialVersionUID = 3289293029441427248L;    public SpiralCanvas() {        super();        this.setSize(200, 200);    }    public void paint(Graphics g) {        double r;        int x;        int y;        int centreOffset = 100;        int oldx = centreOffset;        int oldy = centreOffset;        double PI = 3.141593;        double scaleFactor = 2.0;        // colour        g.setColor(Color.red);        for (double i = 0; i < 16 * PI; i += 0.1) {            r = scaleFactor * i;            x = (int)(r * Math.cos(i)) + centreOffset;            y = (int)(r * Math.sin(i)) + centreOffset;            g.drawLine(oldx, oldy, x, y);            // update previous location            oldx = x;            oldy = y;        }    }}